import React, { useState, useEffect,useRef } from 'react';
import './css/nav_component.scss';
import { Link } from "react-awesome-slider/dist/navigation";
import { toAbsoluteUrl } from '../../_helpers/AssetsHelpers';

function NavComponent(props) {
    const [slug, setSlug] = useState("/");
    let textClass = props.theme === 'dark' ? 'nav_text_dark' : 'nav_text_light';
    let navTheme = props.theme === 'dark' ? 'theme_dark' : 'theme_light';


    const [isActive, setActive] = useState(false);
    const [drop, setDrop] = useState('hidden')

    const [styleSolution, setStyleSolution] = useState({display: 'none'});
    const [style, setStyle] = useState({display: 'none'});
    const [stylePortfolio, setStylePortfolio] = useState({display: 'none'});
    useEffect(() => {
        setSlug(window.location.pathname);
    }, [window.location.pathname]);



    return (
        <div className="nav_links_div">
            <input className="menu-btn" type="checkbox" id="menu-btn" />
            <label className="menu-icon" htmlFor="menu-btn"><span className={`${navTheme} navicon`}></span></label>

            <ul className={`${textClass} menu`}>
                <li className="nav_logo_li"  >
                    <Link href="/">
                        <img className="logo_img" src={`${toAbsoluteUrl(`/media/images/logo_dark.svg`)}`} alt={process.env.REACT_APP_NAME} />
                    </Link>
                </li>

                <li  onMouseEnter={e => {setStyle({visibility: 'hidden', display:'none'})}}  onMouseLeave={e => {setStyle({visibility: 'visible', display:'flex'})}}  className="liPortfolio">
                    <Link 
                        href="/"
                        className={(slug === "" || slug === "/" || slug === "/portfolio-two" || slug === "/portfolio-three") ? "selected" : null}
                    >
                        Portfolio
                    </Link>
                    <ul style={stylePortfolio}  className="dropdown">
                        <li><Link href="/">Unter Port</Link></li>
                        <li><Link href="/">Unter Port2</Link></li>
                    </ul>
                </li>


                <li onMouseEnter={e => {setStylePortfolio({visibility: 'hidden', display:'none'})}}  className="liSolution">
                    <Link 
                        href="/solutions"
                        className={slug === "/solutions" ? "selected" : null}
                    >
                        Solutions
                    </Link>
                    <ul style={style}  className="dropdown2">
                        <li><Link href="/">Unter Port</Link></li>
                        <li><Link href="/">Unter Port2</Link></li>
                    </ul>
                </li>
                <li>
                    <Link 
                        href="/agency"
                        className={slug === "/agency" ? "selected" : null}
                    >
                        Our Agency
                    </Link>
                </li>
                <li>
                    <Link 
                        href="/contact-us"
                        className={slug === "/contact-us" ? "selected" : null}
                    >
                        Contact Us
                    </Link>
                </li>
            </ul>
        </div>
    );
}

export default NavComponent;
