import React from 'react';
import { useLocation } from 'react-router-dom';
import { Provider } from "react-awesome-slider/dist/navigation";
import FullpageSlider from './fullpage';
import './css/master.scss';
// import withAutoplay from 'react-awesome-slider/dist/autoplay';
// import AnimationStyles from 'react-awesome-slider/src/styled/fold-out-animation/fold-out-animation.scss';
// const AutoplaySlider = withAutoplay(AwesomeSlider);

console.log(window.location.href);
// JAVASCRIPT FOR Scroll
var path = 'http://localhost:3000/' // change to the build path
var path2 = "http://212.227.204.100/"
if (window.location.href == path + 'projects' || window.location.href == path2 + 'projects' || window.location.href == path + 'projects-two' || window.location.href == path2 + 'projects-two' ||  window.location.href == path + 'projects-three' || window.location.href == path2 + 'projects-three' ||window.location.href == path + 'imprint' ||  window.location.href == path2 + 'imprint') {
    console.log("solutions");


} else {


// First we get the viewport height and we multiple it by 1% to get a value for a vh unit
let vh = window.innerHeight * 0.01;
// Then we set the value in the --vh custom property to the root of the document
document.documentElement.style.setProperty('--vh', `${vh}px`);
// We listen to the resize event
window.addEventListener('resize', () => {
    // We execute the same script as before
    let vh = window.innerHeight * 0.01;
    document.documentElement.style.setProperty('--vh', `${vh}px`);
  });

var wheel_timer = 0

window.addEventListener('wheel', function (e) {
    clearTimeout(wheel_timer);
    wheel_timer = setTimeout(function () {
        wheel(e)
    }, 50)
});

function wheel(e) {

   var delta = e.deltaY;
    if (delta > 0) {
        var sl = document.querySelector('[aria-label="next"]');
            sl.click();
            console.log("clcik")
            console.log('scrolled down')
    } else {
        var sl = document.querySelector('[aria-label="previous"]');

            sl.click();
            console.log("clcik")
            console.log('scrolled up')
    }
}


    window.addEventListener('touchmove', function (event) {
        if (event.deltaY < 0) {
            var sl1 = document.querySelector('[aria-label="previous"]');
            sl1.click();
        }
        else if (event.deltaY > 0) {
            var sl2 = document.querySelector('[aria-label="next"]');
            sl2.click();

        }


    });



}




function Master(props) {
    const location = useLocation();
    const slug = location.pathname || "";    // slug(page) at which website should begin
    return (<>
        <Provider slug={slug}>
            <div className=" homepage_div">
                <FullpageSlider />
            </div>
        </Provider>
    </>);
}

export default Master;
