import React, { useRef } from "react";
import AwesomeSlider from "react-awesome-slider";
import CoreStyles from 'react-awesome-slider/src/core/styles.scss';
import { toAbsoluteUrl } from "../../_helpers/AssetsHelpers";
import AwesomeSliderStyles from 'react-awesome-slider/src/styled/scale-out-animation/scale-out-animation.scss';
import {
    withNavigationHandlers,
    withNavigationContext
} from "react-awesome-slider/dist/navigation";
import { media } from "./media";
import Solutions from "../Solutions/Solutions";

const Slider = withNavigationHandlers(AwesomeSlider);
const buttonStyle = {

    backgroundImage: <svg width="80" height="1" viewBox="0 0 80 1" fill="none" xmlns="http://www.w3.org/2000/svg">
    <rect width="80" height="1" transform="matrix(1 0 0 -1 0 1)" fill="#FF5A2F"/>
    </svg>

};

export default withNavigationContext(() => {
    const isFirstLoad = useRef(true);


    return (
        <Slider
            className="awesome-slider"
            animation="scaleOutAnimation"
            cssModule={[CoreStyles, AwesomeSliderStyles]}
            organicArrows={false}
            buttonContentRight={<p style={buttonStyle}>next</p>}
            buttonContentLeft={<p style={buttonStyle}>prev</p>}
            media={media}
            // startupScreen={<Solutions />}
            // startupDelay={100}
            transitionDelay={100}
        // onTransitionEnd={(e) => {
        //     var video = e.currentSlide.querySelector("video");
        //     if (video !== null) {
        //         video.load();
        //     }
        // }}
        />
    );
});
