import React, { useRef, useState } from 'react';
import { toAbsoluteUrl } from '../../_helpers/AssetsHelpers';
import Header from '../Layout/Header';
import LogoComponent from '../Layout/LogoComponent';
import NavComponent from '../Layout/NavComponent';
import './css/solutions.scss';
import { Link } from "react-awesome-slider/dist/navigation";

function Solutions(props) {

const [clicked, setClicked] = useState(false);
const ref = useRef(null);
const refIcon = useRef(null);
const refImg = useRef(null);
const refDesc = useRef(null);

const [active, setActiveState] = useState(false);
console.log(active)

function toggleClass() {
    const currentState = active;
    setActiveState(!active);
    console.log(currentState)
};


function closePop(){
    const p = ref.current;
    p.className = "hidden";

    const pIcon = refIcon.current;
    pIcon.className = "showLabel";

    const pImg = refImg.current;
    pImg.className = "showImg";


}

function openPop(){
    const p = ref.current;
    p.className = "show";
    p.className = "icon_description";




}


 const [slug, setSlug] = useState("/contact-us"); 
    return (
        <div className="solutions_div">
            <LogoComponent theme="dark" />

            <div className="solutions_body_div">
                <div className="title_div">
                    <span className="sub_title">Full Service Agency</span>
                    <span className="title">Our Solutions</span>
                </div>

                <div className="icons_div">
                    <div className="icons_div_one">
                        <span className="icon_span">
                            <img src={`${toAbsoluteUrl('/media/icons/advice.svg')}`} alt="Erstgespräch" />
                            <p className="icon_label">Erstgespräch</p>
                            <p className="icon_description">
                                <span className="description_title">Erstgespräch</span>
                                <span className="description_text">In einem ersten unverbindlichen Beratungsgespräch möchten wir vor allem Sie als Unternehmen kennen- und verstehen lernen und besprechen die Ziele Ihrer geplanten Kampagne. Hier werden erste Grundsteine für Ihr Video gelegt. Um den Erfolg Ihrer Kampagne messbar machen zu können erfragen wir bereits hier gezielt, welche Punkte Ihnen dabei am wichtigsten sind. Alles unverbindlich und vertraulich.</span>
                            </p>
                        </span>

                        <span className="icon_span">
                           <a onClick={openPop}></a> <img src={`${toAbsoluteUrl('/media/icons/creativity.svg')}`} alt="Konzept" />
                            <p className="icon_label">Konzept</p>
                            <p className="icon_description">
                                <span className="description_title">Konzept</span>
                                <span className="description_text">Unser Kreativteam arbeitet in Zusammenarbeit mit unserer Marketingabteilung ein maßgeschneidertes Konzept für Sie aus. Immer im Fokus: Das Ergebnis soll in einer lauten und vernetzten Welt herausstechen. Sie erhalten ein schriftliches Angebot und entscheiden sich für das Konzept oder eine der Alternativen, die wir im Angebot inkludieren.</span>
                            </p>
                        </span>

                        <span className="icon_span">
                            <a onClick={openPop}><img  ref={refImg}  src={`${toAbsoluteUrl('/media/icons/video.svg')}`} alt="Produktion" /> </a>
                            <p  ref={refIcon}  className="icon_label">Produktion</p>
                            <p ref={ref} className="icon_description">
                            <a   onClick={closePop} className="close"></a>
                                <span className="description_title">Produktion</span>

                                <span  className="description_text"> Unser Kreativteam arbeitet in Zusammenarbeit mit unserer Marketingabteilung ein maßgeschneidertes Konzept für Sie aus. Immer im Fokus: Das Ergebnis soll in einer lauten und vernetzten Welt herausstechen. Sie erhalten ein schriftliches Angebot und entscheiden sich für das Konzept oder eine der Alternativen, die wir im Angebot inkludieren.</span>
                            </p>
                        </span>
                    </div>

                    <div className="icons_div_two">
                        <span className="icon_span">
                        <a onClick={openPop}> <img onClick={toggleClass} className={` ${active ? 'showImg': null}`}  src={`${toAbsoluteUrl('/media/icons/film-editing.svg')}`} alt="Post-Produktion" /> </a>
                            <p   className={`icon_label ${active ? 'showLabel': null}`}>Post-Produktion</p>
                            <p  className={`icon_description ${active ? 'hidden': null}`}>
                            <a   onClick={toggleClass} className="close"></a>
                                <span className="description_title">Post-Produktion</span>
                                <span className="description_text">Unser Kreativteam arbeitet in Zusammenarbeit mit unserer Marketingabteilung ein maßgeschneidertes Konzept für Sie aus. Immer im Fokus: Das Ergebnis soll in einer lauten und vernetzten Welt herausstechen. Sie erhalten ein schriftliches Angebot und entscheiden sich für das Konzept oder eine der Alternativen, die wir im Angebot inkludieren.</span>

                            </p>

                        </span>

                        <span className="icon_span">
                            <img src={`${toAbsoluteUrl('/media/icons/cloud-computing.svg')}`} alt="Übergabe" />
                            <p className="icon_label">Übergabe</p>
                            <p className="icon_description">
                                <span className="description_title">Übergabe</span>
                                <span className="description_text">Unser Kreativteam arbeitet in Zusammenarbeit mit unserer Marketingabteilung ein maßgeschneidertes Konzept für Sie aus. Immer im Fokus: Das Ergebnis soll in einer lauten und vernetzten Welt herausstechen. Sie erhalten ein schriftliches Angebot und entscheiden sich für das Konzept oder eine der Alternativen, die wir im Angebot inkludieren.</span>
                            </p>
                        </span>

                        <span className="pagination_span">
                            <p className="mb-0">Start today</p>
                            <p className="contact_email">{process.env.REACT_APP_EMAIL}</p>
                            <Link 
                        href="/contact-us"
                        className={slug === "/contact-us" ? "selected" : null}
                    > <img src={`${toAbsoluteUrl('/media/icons/arrow-right.svg')}`}  alt="Right" /></Link>
                        </span>

                        {/* <span className="icon_span">
                            <div><img src={`${toAbsoluteUrl('/media/icons/advice.svg')}`} alt="advice" /></div>
                            <div className="mt-2 mx-2"><label>Erstgespräch</label></div>
                        </span> */}
                    </div>
                </div>
            </div>

            <NavComponent theme="dark" />
        </div>
    );
}

export default Solutions;
