import React from 'react';
// import HorizontalScroll from 'react-scroll-horizontal';


import 'react-awesome-slider/dist/styles.css';
import withAutoplay from 'react-awesome-slider/dist/autoplay';
import AwesomeSlider from 'react-awesome-slider';
import CoreStyles from 'react-awesome-slider/src/core/styles.scss';
//import AnimationStyles from 'react-awesome-slider/src/styled/fold-out-animation/fold-out-animation.scss';
import AwesomeSliderStyles from 'react-awesome-slider/src/styled/scale-out-animation/scale-out-animation.scss';



import { toAbsoluteUrl } from '../../_helpers/AssetsHelpers';
// import Header from '../Layout/Header';
import './css/master.scss';
import Solutions from '../Solutions/Solutions';
import Agency from '../Agency/Agency';
import ContactUs from '../ContactUs/ContactUs';
import LogoComponent from '../Layout/LogoComponent';
import NavComponent from '../Layout/NavComponent';


const AutoplaySlider = withAutoplay(AwesomeSlider);




window.addEventListener('wheel', function (event) {
    if (event.deltaY < 0) {
        var sl = document.querySelector('[aria-label="previous"]');
        sl.click();
    }
    else if (event.deltaY > 0) {
        var sl = document.querySelector('[aria-label="next"]');
        sl.click();
    }
});

window.addEventListener('touchmove', function (event) {
    if (event.deltaY < 0) {
        var sl = document.querySelector('[aria-label="previous"]');
        sl.click();
    }
    else if (event.deltaY > 0) {
        var sl = document.querySelector('[aria-label="next"]');
        sl.click();
    }
});



function Master(props) {



    const child1 = { width: `100%`, height: `100%`, backgroundColor: 'red' }
    const child2 = { width: `100%`, height: `100%`, backgroundColor: '#F1F6F8' }
    const child3 = { width: `100%`, height: `100%`, backgroundColor: '#000000' }
    const child4 = { width: `100%`, height: `100%`, backgroundColor: '#576061' }

    const slider = (
        <AwesomeSlider

            animation="scaleOutAnimation"
            cssModule={[CoreStyles, AwesomeSliderStyles]}

            onTransitionEnd={(e) => {
                var video = e.currentSlide.querySelector("video");
                if (video !== null) {
                    video.load();
                }
            }}

        >
            <div className="homepage_content" style={{ width: '100%', height: `100vh` }}><a name="p1"></a>
                <LogoComponent theme="light" showBottomNav={true} subTitle="Fashion Advertisement" title={["Post Production and ", <br></br>,  "Cinematic for Louis Vuitton"]} />

                <div className="video_div">
                    <video
                        autoPlay
                        loop
                        muted
                        style={{
                            position: "absolute",
                            width: "100%",
                            left: "50%",
                            top: "50%",
                            height: "100%",
                            objectFit: "cover",
                            transform: "translate(-50%,-50%)",
                            // opacity: 0.5
                            // zIndex: "-1"
                        }}>
                        <source src={toAbsoluteUrl("/media/video/video_1.mp4")} type="video/mp4"></source>
                    </video>
                </div>

                <NavComponent theme="light" />
            </div>

            <div className="homepage_content" style={{ width: '100%', height: `100vh` }}>
                <LogoComponent theme="light" showBottomNav={true} subTitle="Cinematic Trailer" title={["Video Production", <br></br>,  "and Post Production"]}  />

                <div className="video_div">
                    <video
                        autoPlay
                        loop
                        muted
                        style={{
                            position: "absolute",
                            width: "100%",
                            left: "50%",
                            top: "50%",
                            height: "100%",
                            objectFit: "cover",
                            transform: "translate(-50%,-50%)",
                        }}>
                        <source src={toAbsoluteUrl("/media/video/video_2.mp4")} type="video/mp4"></source>
                    </video>
                </div>

                <NavComponent theme="light" />
            </div>

            <div className="homepage_content" style={{ width: '100%', height: `100vh` }}>
                <LogoComponent theme="light" showBottomNav={true} subTitle="Fashion Advertisement test" title={["Cinematic and", <br></br>,  "Design for Hermes"]} />

                <div className="video_div">
                    <video
                        autoPlay
                        loop
                        muted
                        style={{
                            position: "absolute",
                            width: "100%",
                            left: "50%",
                            top: "50%",
                            height: "100%",
                            objectFit: "cover",
                            transform: "translate(-50%,-50%)",
                        }}>
                        <source src={toAbsoluteUrl("/media/video/video_3.mp4")} type="video/mp4"></source>
                    </video>
                </div>

                <NavComponent theme="light" />
            </div>

            <div style={child2}>
                <Solutions />
            </div>

            <div style={child3}>
                <Agency />
            </div>

            <div style={child4}>
                <ContactUs />
            </div>
        </AwesomeSlider>
    );

    return (<>
        <div
            className=" homepage_div"
        >
            {slider}
        </div>
    </>);
}

export default Master;
